pragma solidity ^0.8.0;

contract Whitelist {
    // Mapping pour stocker les adresses autorisées
    mapping(address => bool) public whitelist;

    // Événement émis lorsqu'une adresse est autorisée
    event Authorized(address _address);

    // Modifier pour vérifier si l'adresse est autorisée
    modifier check(address _address) {
        require(whitelist[_address], "Address is not authorized");
        _;
    }

    // Fonction pour autoriser une adresse en l'ajoutant au mapping
    function authorize(address _address) public {
        whitelist[_address] = true;
        emit Authorized(_address);
    }

    function testCheck(address _address) public view check(_address) returns (bool) {
        return true;
    }

}
